--
-- Create procedure `proc_get_total_product`
--
CREATE DEFINER = 'root'@'localhost'
PROCEDURE proc_get_total_product (IN Fil nvarchar(255), IN CategoryId char(36), IN Price double)
BEGIN
  IF (Price > 0) THEN
    SELECT
      COUNT(*)
    FROM product p
    WHERE ((Fil IS NULL)
    OR (p.ProductName LIKE CONCAT('%', Fil, '%')))
    AND ((CategoryId IS NULL)
    OR (p.ProductCategoryId LIKE CONCAT('%', CategoryId, '%')))
    AND (p.ProductSalePrice / 1000000 <= Price);
  END IF;
  IF (Price = 0) THEN
    SELECT
      COUNT(*)
    FROM product p
    WHERE ((Fil IS NULL)
    OR (p.ProductName LIKE CONCAT('%', Fil, '%')))
    AND ((CategoryId IS NULL)
    OR (p.ProductCategoryId LIKE CONCAT('%', CategoryId, '%')));
  END IF;
END
$$

--
-- Create procedure `proc_get_products_filter`
--
CREATE DEFINER = 'root'@'localhost'
PROCEDURE proc_get_products_filter (IN PageIndex int, IN PageSize int, IN Fil nvarchar(255), IN SortStatus int, IN FieldSort varchar(30), IN CategoryId char(36), IN Price double)
BEGIN
  DECLARE start int;
  DECLARE end int;
  SET START = ((PageIndex - 1) * PageSize);
  SET end = ((PageIndex - 1) * PageSize) + PageSize;
  IF (Price > 0) THEN
    IF (SortStatus = 1) THEN
      SELECT
        *
      FROM product p
      WHERE ((Fil IS NULL)
      OR (p.ProductName LIKE CONCAT('%', Fil, '%'))
      OR (p.ProductCategoryId LIKE CONCAT('%', Fil, '%')))
      AND ((CategoryId IS NULL)
      OR (p.ProductCategoryId LIKE CONCAT('%', CategoryId, '%')))
      AND (p.ProductSalePrice / 1000000 <= Price)

      ORDER BY p.ProductSalePrice DESC
      LIMIT start, PageSize;
    END IF;
    IF (SortStatus = 2) THEN
      SELECT
        *
      FROM product p
      WHERE ((Fil IS NULL)
      OR (p.ProductName LIKE CONCAT('%', Fil, '%')))
      AND ((CategoryId IS NULL)
      OR (p.ProductCategoryId LIKE CONCAT('%', CategoryId, '%')))
      AND (p.ProductSalePrice / 1000000 <= Price)
      ORDER BY p.ProductSalePrice ASC
      LIMIT start, PageSize;
    END IF;
    IF (SortStatus != 1
      || SortStatus != 2
      || SortStatus = NULL) THEN
      SELECT
        *
      FROM product p
      WHERE ((Fil IS NULL)
      OR (p.ProductName LIKE CONCAT('%', Fil, '%')))
      AND ((CategoryId IS NULL)
      OR (p.ProductCategoryId LIKE CONCAT('%', CategoryId, '%')))
      AND (p.ProductSalePrice / 1000000 <= Price)
      LIMIT start, PageSize;
    END IF;
  END IF;
  IF (Price = 0) THEN
    IF (SortStatus = 1) THEN
      SELECT
        *
      FROM product p
      WHERE ((Fil IS NULL)
      OR (p.ProductName LIKE CONCAT('%', Fil, '%'))
      OR (p.ProductCategoryId LIKE CONCAT('%', Fil, '%')))
      AND ((CategoryId IS NULL)
      OR (p.ProductCategoryId LIKE CONCAT('%', CategoryId, '%')))
      ORDER BY p.ProductSalePrice DESC
      LIMIT start, PageSize;
    END IF;
    IF (SortStatus = 2) THEN
      SELECT
        *
      FROM product p
      WHERE ((Fil IS NULL)
      OR (p.ProductName LIKE CONCAT('%', Fil, '%')))
      AND ((CategoryId IS NULL)
      OR (p.ProductCategoryId LIKE CONCAT('%', CategoryId, '%')))
      ORDER BY p.ProductSalePrice ASC
      LIMIT start, PageSize;
    END IF;
    IF (SortStatus != 1
      || SortStatus != 2
      || SortStatus = NULL) THEN
      SELECT
        *
      FROM product p
      WHERE ((Fil IS NULL)
      OR (p.ProductName LIKE CONCAT('%', Fil, '%')))
      AND ((CategoryId IS NULL)
      OR (p.ProductCategoryId LIKE CONCAT('%', CategoryId, '%')))
      ORDER BY p.ModifiedDate DESC
      LIMIT start, PageSize;
    END IF;
  END IF;
END
$$

