BEGIN
  DECLARE start int;
  DECLARE end int;
  DECLARE odID char(36);
  SET START = ((PageIndex - 1) * PageSize);
  SET end = ((PageIndex - 1) * PageSize) + PageSize;
  SET odID = (SELECT
