INSERT INTO recruitment VALUES
('03f4118b-5894-4c4b-bcdf-c70c25def9cd', 'Tuyển dụng nhân viên bán hàng', 'Mô tả công việc\r\n- Hiểu rõ và thuộc tính năng, giá, ưu nhược điểm của sản phẩm, sản phẩm tương tự, sản phẩm của đối thủ cạnh tranh.\r\n- Thu nhập và đánh giá thông tin về cạnh tranh, về khách hàng và thị trường.\r\n- Tiếp xúc khách hàng và ghi nhận toàn bộ các thông tin của khách hàng trong báo cáo tiếp xúc khách hàng. Báo cáo nội dung tiếp xúc khách hàng trong ngày cho Trưởng phòng.\r\n- Theo dõi quá trình thanh lý hợp đồng, hỗ trợ phòng kế toán đốc thúc công nợ, chỉ xong trách nhiệm khi khách hàng đã thanh toán xong.\r\n- Tìm hiểu nhu cầu của khách hàng. Tìm kiếm khách hàng tiềm năng.', 'tuyendung1.png', '2021-09-19 00:00:00', '2021-09-19 00:00:00', 1, 0),
('6e77f5cf-7614-4e80-a0bd-6b63dfc84e0b', 'TUYỂN DỤNG NHÂN VIÊN HÀNH CHÍNH VĂN PHÒNG', 'Mô tả công việc\r\n- Soạn thảo các văn bản chung của Công ty.\r\n- Thực hiện công tác BHXH, BHYT của Công ty đảm bảo việc tuân thủ đúng các chế độ chính sách liên quan và quyền lợi của người lao động\r\n- Theo dõi, kiểm soát tình hình biến động nhân lực, ký HĐTV, HĐLĐ trong toàn công ty.\r\n- Thực hiện một số nhiệm vụ khác theo yêu cầu của cấp trên.', 'tuyendung2.png', '2021-09-19 00:00:00', '2021-09-19 00:00:00', 1, 1),
('958c9352-4280-4225-aa5b-2ce270f5d48f', 'TUYỂN DỤNG CÔNG NHÂN SẢN XUẤT', 'Mô tả công việc\r\n- Vị trí tuyển dụng: Công nhân sản xuất\r\n- Số lượng tuyển: 20 người (Nam), độ tuổi từ 20 đến 40.\r\n- Thời gian làm việc: Theo ca', 'tuyendung3.png', '2021-09-19 00:00:00', '2021-09-19 00:00:00', 1, 1),
('f70194f3-dbef-4a3c-ba5f-27f20d6fc1dc', 'Tuyển dụng nhân viên IT', 'Đảm bảo hệ thống Website hoạt động thông suốt.', 'tuyendung4.png', '2021-09-19 00:00:00', '2021-09-19 00:00:00', 0, 0);

